        using ll = long long;
        ll cur = 0;
        ll res = 0;
        for (int i = 0; i < n; ++i)
        {
            b[i] -= a[i];
            ll x = i - 1 >= 0 ? b[i - 1] : 0;
            ll y = b[i];
            if (x <= 0 && y <= 0) x = -x, y = -y;
            if (x >= 0 && y >= 0)
            {
                if (y >= x)
