class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        arr = [target[i] - nums[i] for i in range(len(nums))]
        temp, flag = [], arr[0]
        self.ans = 0

        def ff(a):
            if not a:
                return
            if a[0] < 0:
                a = [-i for i in a]
                m = min(a)
            else:
                m = min(a)
            self.ans += m
            a2, t2 = [], []
            for i in a:
                i -= m
                if i == 0:
                    a2.append(t2)
                    t2 = []
                else:
                    t2.append(i)
            a2.append(t2)
            for i in a2:
                ff(i)

        for i in range(len(arr)):
            if flag * arr[i] > 0:
                temp.append(arr[i])
            else:
                ff(temp)
                temp = [arr[i]]
                flag = arr[i]
        ff(temp)
        return self.ans
