func minimumOperations(nums []int, target []int) int64 {
    for i := range nums {
        nums[i] -= target[i]
    }
    res, left := 0, 0
    for left < len(nums) {
        for left < len(nums) && nums[left] == 0 {
            left++
        }
        if left == len(nums) {
            break
        }
        d := nums[left] > 0
        mn := nums[left]
        right := left
        for right < len(nums) && nums[right] > 0 == d && nums[right] != 0 {
            if d {
                mn = min(mn, nums[right])
            } else {
                mn = max(mn, nums[right])
            }
            right++
        }
        for i := left; i < right; i++ {
            nums[i] -= mn
        }
        if mn > 0 {
            res += mn
        } else {
            res -= mn
        }
    }
    return int64(res)
}
