class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> vec;
        for (int i = 0; i < nums.size(); i++) {
            if (i) {
                vec.push_back(target[i] + nums[i - 1] - nums[i] - target[i - 1]);
            }
            else vec.push_back(target[0] - nums[0]);
        }

        long long r1 = 0, r2 = 0;
        for (int n : vec) {
            if (n > 0) r1 += n;
            else r2 += n;
        }

        return max(r1, -r2);
    }
};
