class Solution {
public:
    long long cal (vector<pair<int, int>> &mono, vector<int> &diff){
       long long ans = 0;
       for(auto &x : mono){
            int dec = 0;
            int st = x.first;
            int en = x.second;
            for(int i = st; i <= en; i++){
                int req = abs(diff[i]);
                if(req-dec>=0){
                    ans+=(req-dec);
                    dec = req;
                }else{
                    dec=req;
                }
            }
       }
       return ans;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<pair<int, int>>mono;
        vector<pair<int, int>>monop;
        vector<int>diff;
        for(int i = 0; i < n; i++){
            diff.push_back(target[i]-nums[i]);
        }

        int idx = -1;
        for(int i = 0 ; i < n; i++){
            if(diff[i]<0&&idx==-1){
                idx = i;
            }else if(idx!=-1 && diff[i]>=0){
                mono.push_back({idx, i-1});
                idx = -1;
            }
        }
        if(diff[n-1]<0){
            mono.push_back({idx, n-1});
        }

        idx = -1;
        for(int i = 0 ; i < n; i++){
            if(diff[i]>0&&idx==-1){
                idx = i;
            }else if(idx!=-1 && diff[i]<=0){
                monop.push_back({idx, i-1});
                idx = -1;
            }
        }
        if(diff[n-1]>0){
            monop.push_back({idx, n-1});
        }

        return cal(monop, diff) + cal(mono, diff);
    }
};
