    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n=a.size();
        vector<int>c(n);
        for(int i=0;i<n;i++)
            c[i]=a[i]-b[i];

        long long ans=0;
        int pt=0;

        for(int i=1;i<n;i++)
        {
            if((c[i]>0) != (c[i-1]>0))
            {
                ans+=f(pt,i-1,c,c[i-1]>0);
                pt=i;
            }
        }
        ans+=f(pt,n-1,c,c[n-1]>0);
        return ans;

    }
