class Solution {
public:
    long long increase(vector<int>& nums, int left, int right){  // 左闭右开
        if(left >= right) return 0;

        long long ans = 0;
        int mi = *min_element(nums.begin() + left, nums.begin() + right);
        ans += mi;

        for(int i = left; i < right; i++){
            nums[i] -= mi;

            if(nums[i] == 0){
                ans += increase(nums, left, i);
                left = i + 1;
            }
        }
        ans += increase(nums, left, right);

        return ans;
    }

    long long decrease(vector<int>& nums, int left, int right){ // 左闭右开
        if(left >= right) return 0;

        long long ans = 0;
        int ma = *max_element(nums.begin() + left, nums.begin() + right);
        ans -= ma;

        for(int i = left; i < right; i++){
            nums[i] -= ma;

            if(nums[i] == 0){
                ans += decrease(nums, left, i);
                left = i + 1;
            }
        }
        ans += decrease(nums, left, right);

        return ans;
    }


    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for(int i = 0; i < n; i++){
            nums[i] = target[i] - nums[i];
        }

        long long ans = 0;
        int left = 0;
        bool sign = true;  // true表示正数，false表示负数

        for(int i = 0; i < n; i++){
            if(nums[i] == 0){
                if(sign){
                    ans += increase(nums, left, i);
                    left = i + 1;
                }
                else{
                    ans += decrease(nums, left, i);
                    left = i + 1;
                }
            }
            else if(sign == true && nums[i] < 0){
                ans += increase(nums, left, i);
                left = i;
                sign = false;
            }
            else if(sign == false && nums[i] > 0){
                ans += decrease(nums, left, i);
                left = i;
                sign = true;
            }
        }

        if(sign){
            ans += increase(nums, left, nums.size());
        }
        else{
            ans += decrease(nums, left, nums.size());
        }

        return ans;
    }
};
