class Solution {
public:

      long long minCost(vector<int>& target) {
        vector<int> stack;
         long long res = 0;

        stack.push_back(target[0]);
        for (int i = 1; i < target.size(); i++) {
            if (target[i] > stack.back()) {
                stack.push_back(target[i]);
                continue;
            } 
            if (target[i] == stack.back())
                continue;

            int top = stack.back();

            while (!stack.empty() && target[i] < stack.back())
                stack.pop_back();

            res += top - target[i];
            stack.push_back(target[i]);
        }

        if (!stack.empty())
            res += stack.back();

        return res;
    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
         long long n = nums.size();
        vector<int>V(n,0);
        for(int i = 0;i < nums.size();i++){
            V[i] = (nums[i] - target[i]);
        }
        int i = 0;
         long long res = 0;
        while(i < n){
            vector<int>p;
            if(V[i] < 0){
                while(i < n && V[i] < 0){p.push_back(abs(V[i]));i++;}
                res += minCost(p);
            }else if(V[i] > 0){
                while(i < n && V[i] > 0){p.push_back(abs(V[i]));i++;}
                res += minCost(p);
            }
            else i++;
        }
        return res;
    }
};
