#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
typedef vector<int> vi;
typedef pair<int,int> pii;
typedef pair<double, double> pdd;
#define pb push_back
#define mp make_pair
#define fs first
#define sc second
#define rep(i, from, to) for (int i = from; i < (to); ++i)
#define all(x) x.begin(), x.end()
#define sz(x) (int)(x).size()
#define FOR(i, to) for (int i = 0; i < (to); ++i)
typedef vector<vector<int> > vvi;
typedef vector<ll> vll;
typedef vector<vll> vvll;
typedef vector<pair<int, int> > vpi;
typedef pair<ll,ll> pll;
typedef vector<string> vs;
const char en = '\n';

class Solution {
public:
    long long minimumOperations(vector<int>& v1, vector<int>& v2) {
        ll ret = 0;
        vll v;
        FOR(i, sz(v1)) {
            v.pb(v1[i] - v2[i]);
        }
        ll prev = 0;
        FOR(i, sz(v)) {
            if (prev >= 0 && v[i] < 0) {
                ret += abs(v[i]);
            }
            if (prev >= 0 && v[i] > 0) {
                ret += max(v[i] - prev, 0LL);
            }
            if (prev < 0 && v[i] > 0) {
                ret += v[i];
            }
            if (prev < 0 && v[i] < 0) {
                ret += max(0LL, abs(v[i]) - abs(prev));
            }
            prev = v[i];
        }
        return ret;
    }
};
