class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long op = 0;
        int pre = 0;
        for(int i = 0; i < n; i++) {
            int now = target[i] - nums[i];
            if(1ll * now * pre <= 0) {
                op += abs(now);

            } else if(now > 0) {
                op += max(0, now - pre);
            } else if(now < 0) {
                op += max(0, pre - now);
            }
            pre = now;
        }
        return op;
    }
};
