class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        a=[target[i]-nums[i] for i in range(len(nums))]
        print(a)
        ans=abs(a[0])
        cu=0
        x=lambda q :'p' if a[q]>=0 else 'n'
        for i in range(1,len(a)):
            if(x(i)==x(cu)):
                if(abs(a[i])>abs(a[cu])):
                    ans+=abs(a[i])-abs(a[cu])
                cu=i
            else:
                ans+=abs(a[i])
                cu=i
        return ans
