class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int l = 0;
        int d, t;
        for(int i = 0; i < nums.size(); i++){
            d = target[i] - nums[i];
            t = d;
            if((d < 0 && l < 0) || (d >= 0 && l >= 0)){
                if(abs(d) < abs(l)) d = 0;
                else d = abs(d) - abs(l);
            }
            else d = abs(d);
            l = t;
            ans += d;
        }
        return ans;
    }
};
