class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();

        long long last=0, ans=0;

        for(int i=0; i<n; i++){
            long long dif=target[i]-nums[i];

            if(dif>0){
                if(last>0){
                    if(dif>last){
                        ans+=(dif-last);
                    }
                } else{
                    ans+=dif;
                }
            } else{
                if(last<0){
                    if(dif<last){
                        ans+=(last-dif);
                    }
                } else{
                    ans+=abs(dif);
                }
            }

            last=dif;
        }

        // cout << endl;

        return ans;
    }
};
