class Solution {
public:
          using ll = long long ;

    long long minimumOperations(vector<int>& nums, vector<int>& target) {

             ll n = nums.size() ;
             vector<ll>diff(n) ;
             for(ll i = 0 ; i < n ;i ++) diff[i] =  nums[i] -  target[i] ;

             function <ll(ll  , ll , ll)> solve = [&](ll i , ll j , ll mn){
                        //cout << i << " " << j << endl;
                     ll ret = 0 ;
                        for(ll k = i ; k <= j ; k ++){
                              //   cout << diff[k] << " " ;
                                 diff[k] = abs(diff[k]) ;
                                 if(k == i) ret = diff[i] ;
                                 else if(diff[k] >= diff[k - 1]) ret += diff[k] - diff[k - 1] ;
                        }
                    // cout << endl;
                   //  cout << ret <<endl;
                     return ret ;
             };


            ll ans =  0 ; 
            for(ll i = 0 ; i < n ; i ++){
                       ll j = i ;

                     while(j < n && ((diff[j] < 0 && diff[i] < 0) || (diff[i] >= 0 && diff[j] >= 0))){
                              j ++ ;
                     }
                    j -- ;
                   // cout << i << " " << j << endl;
                    ans += solve(i , j , 0) ;
                    i = j ;
            }
            cout << endl;

            return ans;           

    }
};
