class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        long res = 0l, add = 0l, sub = 0l;
        for (int i = 0; i < n; i++) {
            int in = nums[i], it = target[i];
            if (in < it) {
                res += sub;
                sub = 0l;
                if (it - in <= add)
                    res += (add - (it - in));    
                add = it - in;
            }
            else if (in > it) {
                res += add;
                add = 0l;
                if (in - it <= sub)
                    res += (sub - (in - it));
                sub = in - it;
            }
            else {
                res += add + sub;
                add = 0l;
                sub = 0l;
            }
        }
        return res + add + sub;
    }
}
