class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> diffs(n, 0); for (int i = 0; i < n; i++) diffs[i] = target[i] - nums[i];
        long long total = 0;
        long long prev = 0;
        for (long long i : diffs) {
            long long small = min(0LL, prev);
            long long big = max(0LL, prev);

            if (i > big) total += i - big;
            if (i < small) total += small - i;


            prev = i;
            // cout << total << " ";
        }

        return total;
    }
};
