#define LL long long

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();

        vector<LL> arr(n);

        int minVal = 1e9;

        for (int i = 0; i < n; i++)
        {
            arr[i] = target[i] - nums[i];
        }

        LL ans = abs(arr[0]);

        for (int i = 1; i < n; i++)
        {
            if (arr[i] > 0)
            {
                if (arr[i - 1] <= 0)
                {
                    ans += arr[i];
                }
                else
                {
                    ans += max(arr[i] - arr[i - 1], 0LL);
                }
            }
            else if (arr[i] < 0)
            {
                if (arr[i - 1] >= 0)
                {
                    ans -= arr[i];
                }
                else
                {
                    ans += max(arr[i - 1] - arr[i], 0LL);
                }
            }
        }

        return ans;
    }
};
