class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long res = 0;

        int add = 0;
        int minus = 0;
        for (int i = 0; i < nums.length; ++i) {
            if (nums[i] > target[i]) { // 7 minus(3) > 1
                add = 0;
                res += Math.max(0, Math.abs(nums[i] - target[i]) - minus);
                minus =  Math.abs(nums[i] - target[i]);
            } else {
                minus =  0;
                res += Math.max(0, Math.abs(nums[i] - target[i]) - add);
                add =  Math.abs(nums[i] - target[i]);
            }
        }
        return res;

    }
}
