class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        N = len(nums)
        ans, cur = abs(nums[0]-target[0]), target[0]-nums[0]
        for i in range(1, N):
            dif = target[i]-nums[i]
            if dif == 0:
                cur = 0
                continue

            if (dif > 0 and cur < 0) or (dif < 0 and cur > 0):
                ans += abs(dif)
                cur = dif
            else:
                if (abs(cur) < abs(dif)):
                    ans += abs(dif)-abs(cur)
                cur = dif

        return ans
