class Helper {
public:
    long long countOps(vector<int>& target) {
        long long ans = target[0];
        for (int i = 1; i < target.size(); i++) {
            ans += max(0, target[i] - target[i - 1]);
        }
        return ans;
    }
};

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int>a(n, 0);

        long long ans = 0;
        Helper obj;
        for (int i = 0; i < n; i++) {
            a[i] = nums[i] - target[i];
        }

        vector<int>t;
        for (int i = 0; i < n; i++) {
            if (a[i] == 0)continue;
            if (a[i] >= 0) {
                int j = i;
                while (j < n && a[j] >= 0) {
                    t.push_back(a[j]);
                    j++;
                }
                ans += obj.countOps(t);
                t.clear();
                i = j - 1;
            }
            else {
                int j = i;
                while (j < n && a[j] <= 0) {
                    t.push_back(-a[j]);
                    j++;
                }
                ans += obj.countOps(t);
                t.clear();
                i = j - 1;
            }
        }
        return ans;
    }
};
