class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        // 0, 3, 2, -4, 1
        // 0, 4, 2, -4, 2    
        // 0, 1, 2, -1
        // 0, 2, -1, 3
        // 1 2 2
        typedef long long LL;
        const int n = nums.size();
        LL ans = 0;
        LL add_cnt = 0;
        LL decrease_cnt = 0;
        for(int i = n - 1; i > 0; i -- ) {
            nums[i] = nums[i] - nums[i-1];
            target[i] = target[i] - target[i-1];
        }
        for(int i = 0; i < n; i ++ ) {
            if(nums[i] < target[i]) {
                add_cnt += target[i] - nums[i];
            }
            else {
                decrease_cnt += nums[i] - target[i];
            }
            // cout << nums[i] << " " << target[i] << endl;
        }
        ans = max(add_cnt, decrease_cnt);
        return ans;
    }
};
