class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        long long  res=0;
        long long curop=0;
        for(int i=0;i<nums.size();i++)
        {
            if(nums[i]>target[i])
            {
                if(curop<nums[i]-target[i])
                {
                    res+=min((long long)nums[i]-(long long)target[i]-(long long)curop,(long long)nums[i]-(long long)target[i]);
                    curop=nums[i]-target[i];
                }
                else 
                {
                    curop=nums[i]-target[i];
                }
            }
            else 
            {
                if(curop>nums[i]-target[i])
                {
                    res+=min((long long)curop-(long long)nums[i]+(long long)target[i],(long long)target[i]-(long long)nums[i]);
                    curop=nums[i]-target[i];

                }
                else 
                {
                    curop=nums[i]-target[i];

                }

            }
                // cout<<res<<" "<<curop<<endl;;

        }
        return res;
    }
};
