class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long count=0;
        for(int i=0;i<nums.size();i++){
            bool flag=true;
            if(nums[i]==target[i]){
                continue;
            }
            else if(nums[i]>target[i]){
                flag=false;
            }
            int diff=abs(nums[i]-target[i]);
            count+=diff;
            for(int j=i+1;j<nums.size();j++){
                if(!flag && nums[j]>target[j]){
                    if(diff>=(nums[j]-target[j])){
                        diff=min(diff,nums[j]-target[j]);
                        nums[j]=target[j];
                    }
                    else{
                        nums[j]-=diff;
                    }
                }
                else if(flag && nums[j]<target[j]){
                    if(diff>=(target[j]-nums[j])){
                        diff=min(diff,target[j]-nums[j]);
                        nums[j]=target[j];
                    }
                    else{
                        nums[j]+=diff;
                    }
                }
                else{
                    break;
                }
            }
        }
        return count;
    }
};
