impl Solution {
    pub fn minimum_operations(n: Vec<i32>, t: Vec<i32>) -> i64 {
        let mut s = vec![0; n.len()]; 
        for i in 0..n.len() {
            s[i] = t[i] - n[i];
        }

        let mut c: i32 = 0; 
        let mut v: i64 = 0; 
        for i in 0..s.len() {
            if i == 0 || (s[i - 1] > 0 && s[i] > 0) || (s[i - 1] < 0 && s[i] < 0) {
                let aux = (s[i].abs() as i64 - c.abs() as i64);
                if aux > 0 {
                    v += aux;
                }
                c = s[i];
            } else {
                v += s[i].abs() as i64;
                c = s[i];
            }
        }

        v
    }
}
