class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n);

        for(int i = 0; i < n; i++)
        {
            diff[i] = target[i] - nums[i];
        }

        long long ans = abs(diff[0]);        

        for(int i = 1; i < n; i++)
        {
            if(diff[i] == 0) continue;
            bool pre = (diff[i-1]>0);
            bool cur = (diff[i]>0);

            if(pre == cur)
            {
                if(pre) ans += max(0, diff[i]-diff[i-1]);
                else if(diff[i] < diff[i-1]) ans += abs(diff[i]-diff[i-1]);
            }
            else
            {
                ans += abs(diff[i]);
            }
        }
        return ans;
    }
};
