class Solution {
public:

    long long check(vector<int>& a){
        int n = a.size();
        if(a[0] < 0){
            for(int i = 0;i <a.size();++i)a[i] *= -1;
        }
        //for(int i = 0;i <a.size();++i)cout << a[i] << ' ';
        //cout <<'\n';
        long long res = 0;
        res += a[0];
        for (int i = 1; i < n; i++)
            if (a[i] > a[i - 1])
                res += a[i] - a[i-1];
        return res;
    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        vector<vector<int>> p;
        vector<int> dif(nums.size());
        for(int i = 0;i < nums.size();++i){
            dif[i] = nums[i] - target[i];
        }
        p.push_back({});
        for(int i = 0;i < nums.size();++i){
            if(i != 0 && ((dif[i] >= 0 && dif[i-1] <= 0) || (dif[i] <= 0 && dif[i-1] >= 0)))p.push_back({});
            p.back().push_back(dif[i]);
        }
        for(int i = 0;i < p.size();++i){
            ans += check(p[i]);
        }
        return ans;
    }
};
