class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long answer=0;
        int n=nums.size();
        int prev=0;
        for(int i=0;i<n;i++){
            if(nums[i]==target[i]){
                prev=0;
            }
            else if(nums[i]>target[i]){
                if(prev<0){
                    prev=0;
                }
                if(nums[i]-target[i]<=prev){
                    prev=nums[i]-target[i];
                }
                else{
                    answer+=nums[i]-target[i]-prev;
                    prev=nums[i]-target[i];
                }
            }
            else{
                if(prev>0){
                    prev=0;
                }
                if(nums[i]-target[i]<prev){
                    answer+=abs(nums[i]-target[i]-prev);
                    prev=nums[i]-target[i];
                }
                else{
                    prev=nums[i]-target[i];
                }
            }
            // cout<<answer<<" ";
        }
        return answer;
    }
};
