class Solution:
    def minimumOperations(self, nums: List[int], t: List[int]) -> int:
        # 分组循环 + 差分
        a = [x - y for x, y in zip(nums, t)]

        ans = i = 0
        n = len(a)
        while i < n:
            while i < n and a[i] == 0:
                i += 1
            start = i
            while i < n and a[i] * a[start] > 0:
                i += 1
            # 切片
            b = a[start:i]
            # 差分数组
            d = []
            for k in range(len(b)):
                if k == 0:
                    d.append(abs(b[k]))
                else:
                    d.append(abs(b[k]) - abs(b[k - 1]))
            ans += sum(x for x in d if x > 0)

        return ans
