class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = target.size();
        vector<int> diff1(n), diff2(n);
        diff1[0] = nums[0];
        diff2[0] = target[0];
        for (int i = 1; i < n; i++) {
            diff1[i] = nums[i] - nums[i - 1];
            diff2[i] = target[i] - target[i - 1];
        }
        long long d = 0, ans = 0;
        for (int i = 0; i < n; i++) {
            if (diff1[i] < diff2[i]) {
                if (d <= 0) {
                    ans += (long long) diff2[i] - diff1[i] - min((long long) diff2[i] - diff1[i], -d);
                } else {
                    ans += (long long) diff2[i] - diff1[i];
                }
                d += diff2[i] - diff1[i];

            } else {
                if (d >= 0) {
                    ans += (long long) diff1[i] - diff2[i] - min(d, (long long) diff1[i] - diff2[i]);
                } else {
                    ans += (long long) diff1[i] - diff2[i];
                }
                d -= diff1[i] - diff2[i];
            }
        }
        return ans;
    }
};
