class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long now = 0;
        long long res = 0;
        int st = 1;
        int n = nums.size();
        for(int i = 0; i < n; i ++)nums[i]-=target[i];
        now = abs(nums[0]);
        res = now;
        st = nums[0] > 0?1:-1;
        if(!nums[0])st = 0;
        for(int i = 1; i < n; i ++)
        {
            if((nums[i] > 0) && st == 1)
            {
                if(nums[i] > now) 
                {
                    res += 1ll*nums[i] - now;
                    now = nums[i];
                }
                else now = nums[i];
            }
            else if(nums[i] == 0)
            {
                now = 0;
            }
            else if((nums[i] < 0) && st == -1){
                nums[i] = - nums[i];
                if(nums[i] > now)
                {
                    res += 1ll*nums[i] - now;
                    now = nums[i];
                }else now = nums[i];
            }
            else {
                now = abs(nums[i]);
                st = nums[i] > 0? 1 : -1;
                res += now;
            }
        }
        return res;
    }
};
