class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        vector<long long> v;
        for (int i = 0; i < nums.size(); i++) {
            v.push_back((long long)(nums[i] - target[i]));
        }
        // for (auto i : v) {
        //     cout << i << " ";
        // } cout << endl;

        for (int i = 0; i < v.size(); i++) {
            if (v[i] == 0) {
                continue;
            }
            bool isPos1 = v[i] > 0;
            long long ops = abs(v[i]);
            v[i] = 0;
            ans += ops;
            for (int j = i + 1; j < v.size(); j++) {
                bool isPos2 = v[j] > 0;
                if ((v[j] == 0) || isPos1 != isPos2) {
                    // no need to include this, will achieve negative affect
                    break;
                }
                // more than needed
                if (ops >= abs(v[j])) {
                    ops = min(ops, abs(v[j]));
                    v[j] = 0;

                } else {
                    v[j] -= ops * (isPos1 ? 1 : -1);
                }
            }
            // for (auto i : v) {
            //     cout << i << " ";
            // } cout << endl;
        }
        return ans;
    }
