class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0, sum = 0;
        int n = nums.size();
        vector<vector<long long>> vis(n + 10, vector<long long>(2, -1));
        vector<int> arr(n + 10);

        for (int i = 0; i < nums.size(); i++) {
            if (nums[i] + sum != target[i]) {
                arr[i] = nums[i] - target[i];
            }
        }

        ans = abs(arr[0]);
        for (int i = 1; i < arr.size(); i++) {
            ans += abs(arr[i - 1] - arr[i]);
        }

        return ans/2;

    }
};
