class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        d = [t - num for num, t in zip(nums, target)]
        print(d)
        res = abs(d[0])
        for a, b in pairwise(d):
            if a >= 0 and b >= 0:
                res += max(0, b - a)
            elif a <= 0 and b <= 0:
                res += max(0, a - b)
            else:
                res += abs(b)
        return res
