class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n);
        int prev = 0;
        for(int i = 0; i < n; i++){
            diff[i] = target[i] - nums[i];
            int temp = diff[i];
            diff[i] = diff[i] - prev;
            prev = temp;
        }
        long long ops = 0;
        long long offset = 0;
        for(int i = 0; i < n; i++){
            if(diff[i] * offset < 0){
                if(abs(diff[i]) >= abs(offset)){
                    ops += abs(diff[i] + offset);
                    offset = diff[i] + offset;
                }else{
                    offset += diff[i];
                }
            }else{
                ops += abs(diff[i]);
                offset += diff[i];
            }
        }
        return ops;
    }
};
