class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        // Two-pointers
        long long ans = 0;
        int l = 0, r = 0;
        int n = nums.size();
        // Make target => diff
        for (int i = 0; i < n; i++) {
            target[i] -= nums[i];
            // cout << target[i] << ", ";
        }
        // cout << endl;
        while (true) {
            // Skip zero diff as long as possible
            while (l < n && target[l] == 0) {
                l += 1;
            }
            if (l == n) {
                break;
            }

            // l < n

            int diff = target[l];
            // cout << "diff = target[" << l << "] = " << diff << endl;
            if (diff > 0) {
                int min_diff = diff;
                r = l;
                while (r + 1 < n && target[r + 1] > 0) {
                    // cout << "target[" << r + 1 << "] = " << target[r + 1] << endl;
                    min_diff = min(min_diff, target[r + 1]);
                    r += 1;
                }
                // cout << "[" << l << ", " << r << "], diff = " << diff << endl;
                for (int i = l; i <= r; i++) {
                    target[i] -= min_diff;
                }
                ans += min_diff;
                // l += 1;
            } else {
                int max_diff = diff;
                r = l;
                while (r + 1 < n && target[r + 1] < 0) {
                    max_diff = max(max_diff, target[r + 1]);
                    r += 1;
                }
                // cout << "[" << l << ", " << r << "], diff = " << diff << endl;
                for (int i = l; i <= r; i++) {
                    target[i] -= max_diff;
                }
                ans -= max_diff;
                // l += 1;
            }
        }
        return ans;
    }
};
