class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long>arr;
        int n=nums.size();
        for(int i=0;i<n;i++){
            arr.push_back(target[i]-nums[i]);
        }
        long long mx=0,mi=0,sum=0;
        for(int i=0;i<n;i++){
            if(arr[i]>0){
               if(arr[i]>mx){
                sum+=arr[i]-mx;
               }
               mx=arr[i];
                mi=0;

            }
            else if(arr[i]<0){
                if(arr[i]<mi)
                sum+=abs(arr[i]-mi);
                mx=0;
                mi=arr[i];
            }
            else {
                mx=0;
                mi=0;
            }


        }

        return sum;
    }
};
