class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n = a.size();
        long long ans = 0;
        long long op1 = 0;
        long long op2 = 0;
        for(int i=0; i<n; ++i)
        {
            if(a[i]<b[i])
            {
                if((a[i]+op1)>=b[i])
                {
                    op1 = b[i]-a[i];
                }else{

                    ans+=b[i]-a[i]-op1;
                    op1=b[i]-a[i];
                }
                op2 = 0;
            }else if(a[i]>b[i])
            {
                if(a[i]<=(b[i]+op2))
                {
                    op2 = a[i]-b[i];
                }else{
                    ans+=a[i]-b[i]-op2;
                    op2 = a[i]-b[i];
                }
                op1 = 0;
            }else{
                op1 = 0;
                op2 = 0;
            }
            //if(i==1) return op2;
        }
        return ans;
    }
};
