class Solution {
    typedef long long ll;
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int>v(n,0);
        for(int i = 0;i<n;i++){
            v[i] = target[i]-nums[i];
        }
        ll ret = 0;
        int type = 1;//表示为正
        ll sum = 0;
        if(v[0] > 0){
            ret += v[0];
            sum = v[0];
            type = 1;
        }else if(v[0]<0){
            ret -= v[0];
            sum = -v[0];
            type = -1;
        }else{
            type = 0;
        }
        for(int i = 1;i<n;i++){
            if(type == 0){
                if(v[i] > 0){
                    ret += v[i];
                    sum = v[0];
                    type = 1;
                }else if(v[i]<0){
                    ret -= v[i];
                    sum = -v[0];
                    type = -1;
                }else{
                    type = 0;
                }
            }else{
                if(v[i] == 0){
                    type = 0;
                }else{
                    if(type*v[i] <0){
                        if(v[i] > 0){
                            ret += v[i];
                            sum = v[0];
                            type = 1;
                        }else if(v[i]<0){
                            ret -= v[i];
                            sum = -v[0];
                            type = -1;
                        }else{
                            type = 0;
                        }
                    }else{
                        int a = abs(v[i]);
                        int b = abs(v[i-1]);
                        if(a > b){
                            ret += (a-b);
                        }
                    }
                }
            }
        }
        return ret;
    }
};
