class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:

        arr = []
        n = len(nums)
        for i in range(n):
            arr.append(target[i] - nums[i])

        prevInc = 0 
        prevDec = 0 
        ans  = 0 
        for i in range(n):
            if arr[i] == 0 :
                prevInc = prevDec = 0 
            elif arr[i] < 0 :
                prevInc = 0 
                if prevDec < abs(arr[i]):
                    arr[i] = abs(arr[i])
                    ans += arr[i] - prevDec 
                    prevDec = arr[i]
                else :
                    prevDec = abs(arr[i])
            else :
                prevDec = 0  
                if prevInc < arr[i]:
                    ans += arr[i] - prevInc 
                    prevInc = arr[i]

                else :
                    prevInc = arr[i]


        return ans
