class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        int prev = 0;
        long long count = 0;
        int n = nums.size();

        for(int i=0; i<n; i++) {
            int diff = target[i] - nums[i];

            if(prev >=0) {
                if(diff < 0) {
                    count += prev;
                    prev = diff;
                }else{
                    if(diff >= prev) {
                        prev = diff;
                    }else{
                        count += (prev - diff);
                        prev = diff;
                    }
                }
            }else{
                if(diff >= 0) {
                    count += -prev;
                    prev = diff;
                }else{
                    if(diff <= prev) {
                        prev = diff;
                    }else{
                        count += abs(prev - diff);
                        prev = diff;
                    }
                }
            }
        }

        count += abs(prev);

        return count;
    }
};
