class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int>diffe;
        for(int i=0;i<(int)nums.size();i++){
            diffe.push_back(nums[i] - target[i]);
        }
        long long ans = 0;
        long long sign = -2;
        long long cur = 0;
        for(int i=0;i<(int)diffe.size();i++){
            if(sign == -2){
                if(diffe[i] < 0){
                    sign = -1;
                    cur = -diffe[i];
                    ans += -diffe[i];
                }
                else if(diffe[i] > 0){
                    sign = 1;
                    cur = diffe[i];
                    ans += diffe[i];
                }
            }
            else{
                if(sign == -1){
                    if(diffe[i] < 0){
                    if(-diffe[i] > cur){
                        ans += -diffe[i] - cur;
                        cur = -diffe[i];
                    }
                    else{
                        cur = -diffe[i];
                    }
                    }
                    else{
                        sign = -2;
                        i--;
                    }
                }
                else{
                    if(diffe[i] > 0){
                        if(diffe[i] > cur){
                            ans += diffe[i] - cur;
                            cur = diffe[i];
                        }
                        else{
                            cur = diffe[i];
                        }
                    }
                    else{
                        sign = -2;
                        i--;
                    }
                }
            }
        }
        return ans;
    }
};
