class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        diff = [0 for _ in range(n + 2)]
        for index, (i, j) in enumerate(zip(nums, target)):
            diff[index] += (j - i)
            diff[index + 1] -= (j - i)

        cur = 0
        result = 0
        # print(diff)
        for i in range(n + 2):
            if diff[i] > 0:
                result += diff[i]
        return result
