class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        res = 0
        temp = 0
        n = len(nums)
        for i in range(n):
            new_dir = target[i] - nums[i]
            if new_dir * temp <= 0:
                temp = new_dir
                res += abs(temp)
            else:
                if temp >= new_dir > 0:
                    temp = new_dir
                elif new_dir > temp > 0:
                    res += new_dir - temp
                    temp = new_dir
                elif temp < new_dir < 0:
                    temp = new_dir
                else:
                    res += abs(new_dir - temp)
                    temp = new_dir
        return res
