class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long>v(nums.size(),0);
        for(int i=0;i<v.size();++i){
            v[i]=target[i]-nums[i];
        }
        long long ans=0ll;
        for(int i=0;i<v.size();++i){
            if(v[i]>0){
                if(i==0){
                    ans+=v[i];
                }
                else{
                    if(v[i-1]>0){
                        if(v[i-1]<v[i]){
                            ans+=(v[i]-v[i-1]);
                        }
                    }
                    else{
                        ans+=v[i];
                    }
                }
            }
            else{
                if(i==0){
                    ans+=abs(v[i]);
                }
                else{
                    if(v[i-1]<0){
                        if(v[i-1]>v[i]){
                            ans+=abs(v[i]-v[i-1]);
                        }
                    }
                    else{
                        ans+=abs(v[i]);
                    }
                }
            }
        }
        return ans;
    }
};
