class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        int df = 0;
        long long cnt = 0;
        for (int i = 0; i < n; i++){
            if (target[i] > nums[i]){
                if (target[i] > nums[i] + df)
                    cnt += target[i] - max(nums[i], nums[i] + df);
                df = target[i] - nums[i];
            }
            else if (target[i] == nums[i]){
                df = 0;
            }
            else {
                if (target[i] < nums[i] + df)
                    cnt += min(nums[i], nums[i] + df) - target[i];
                df = target[i] - nums[i];
            }
        }
        return cnt;
    }
};
