class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n = a.size();
        vector<int> c(n + 2);
        for (int i = 0; i < n; i ++) {
            c[i+1] = a[i] - b[i];
        }
        long long ans = 0;
        for (int i = 1; i <= n + 1; i ++) {
            ans += abs(c[i] - c[i-1]);
        }
        ans /= 2;
        return ans;

    }
};
