class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        nums.insert(begin(nums), 0);
        target.insert(begin(target), 0);
        vector<int> tmp(n + 1);
        vector<int> v(n + 1);
        for(int i = 1; i <= n; i ++) {
            tmp[i] = target[i] - nums[i];
        }
        for(int i = 1; i <= n; i ++) v[i] = tmp[i] - tmp[i - 1];
        long long s1 = 0, s2 = 0;
        long long res = 0;
        for(int i = 1; i <= n; i ++) {
            if(v[i] > 0) {
                if(s2 >= v[i]) s2 -= v[i];
                else {
                    res += v[i] - s2;
                    s1 += v[i] - s2;
                    s2 = 0;
                }
            } else if(v[i] < 0) {
                v[i] = -v[i];
                if(s1 >= v[i]) s1 -= v[i];
                else {
                    res += v[i] - s1;
                    s2 += v[i] - s1;
                    s1 = 0;
                }
            }
        }
        return res;
    }
};
