class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int len = nums.length;
        long ans = 0L;
        for (int i = 0; i < len; i++) {
            target[i] -= nums[i];
        }
        // System.out.println(Arrays.toString(target));
        int pre = 0;
        for (int i = 0; i < len; i++) {
            if (target[i] > 0) {
                if (pre < 0) {
                    pre = 0;
                }
                ans += pre >= target[i] ? 0 : target[i] - pre;
            } else {
                if (pre > 0) {
                    pre = 0;
                }
                // -3 -2 // -1 -2
                ans += pre <= target[i] ? 0 : pre - target[i];
            }
            pre = target[i];
        }


        return ans;
    }
}
