class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        arr=[a-b for a,b in zip(nums,target)]
        # print(arr)
        ans=abs(arr[0])
        def sign(a):
            return -1 if a<0 else 1
        for i in range(1, len(arr)):
            a, b=abs(arr[i-1]), abs(arr[i])
            s1, s2=sign(arr[i-1]), sign(arr[i])
            if s1 == s2:
                if a < b:
                    ans += b-a 
            else:
                ans += b
        return ans
