using ll = long long;

class Solution {
public:
    ll minimumOperations(
        vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for (int i = 0; i < n; ++i) {
            nums[i] -= target[i];
            // cout << nums[i] << ' ';
        }
        // cout << '\n';
        // 你可以操作 nums 子数组, 一起进行 +/-1, 最终全部为 0 の最少操作数
        // 最少操作数
        // 分类讨论: 相同区间一起搞!? 相差不多呢?

        ll res = 0; // 其实是分组循环! 问当前 abs(区间x)的min
        int run = n;
        while (run) {
            run = false;
            for (int i = 0; i < n; ++i) {
                int add = 1e9, l = i;
                while (i < n && nums[i] > 0) {
                    // cout << nums[i] << ':' << i << '\n';
                    add = min(add, nums[i++]);
                }
                res += (add != 1e9 ? add : 0);
                for (int j = l; j < i; ++j) {
                    if (!(nums[j] -= add))
                        --run;
                    // cout << j << '\n';
                }

                l = i, add = 1e9;
                while (i < n && nums[i] < 0) {
                    // cout << nums[i] << ' ' << i << '\n';
                    add = min(add, -nums[i++]);
                }
                res += (add != 1e9 ? add : 0);
                for (int j = l; j < i; ++j) {
                    if (!(nums[j] += add))
                        --run;
                    // cout << j << ' ';
                }
                if (l != i)
                    --i;
            }
        }

        return res;
    }
};


